contracts/ComplianceRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract ComplianceRegistry is AccessControl {
    bytes32 public constant SCHOLAR_ROLE = keccak256("SCHOLAR_ROLE");

    mapping(uint32 => bool) public sectorAllowed;
    mapping(address => bool) public vendorAllowed;

    // loanId => approved
    mapping(uint256 => bool) public loanApproved;
    // loanId => note CID (off-chain)
    mapping(uint256 => string) public scholarNoteCID;

    event SectorUpdated(uint32 indexed sector, bool allowed);
    event VendorUpdated(address indexed vendor, bool allowed);
    event LoanVerified(uint256 indexed loanId, address indexed scholar, bool approved, string noteCID);

    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(SCHOLAR_ROLE, admin);
    }

    function setSector(uint32 code, bool ok) external onlyRole(DEFAULT_ADMIN_ROLE) {
        sectorAllowed[code] = ok;
        emit SectorUpdated(code, ok);
    }

    function setVendor(address vendor, bool ok) external onlyRole(DEFAULT_ADMIN_ROLE) {
        vendorAllowed[vendor] = ok;
        emit VendorUpdated(vendor, ok);
    }

    function verifyLoan(uint256 loanId, bool approved, string calldata noteCID)
        external onlyRole(SCHOLAR_ROLE)
    {
        loanApproved[loanId] = approved;
        scholarNoteCID[loanId] = noteCID;
        emit LoanVerified(loanId, msg.sender, approved, noteCID);
    }
}

contracts/Loan.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "./utils/PullPayment.sol";
import "./ComplianceRegistry.sol";


contract Loan is PullPayment {
    enum LoanState { PROPOSED, FUNDRAISING, ACTIVE, COMPLETED, DEFAULTED, CANCELLED }
    enum LoanType { MUDARABAH, MURABAHA }

    uint256 public loanId;
    address public borrower;
    IERC20 public stable;
    uint256 public principal;
    uint256 public minFund;
    uint256 public tenorDays;
    uint16 public profitShareBps;
    uint16 public markupBps;
    uint32 public sectorCode;
    LoanType public loanType;

    LoanState public state;
    uint256 public funded;
    uint256 public activatedAt;
    uint256 public repaid;

    mapping(address => uint256) public contributions;
    address[] public investors;

    address public compliance;

    event Funded(address indexed investor, uint256 amount, uint256 fundedTotal);
    event Activated(uint256 at);
    event RepaymentMade(address indexed payer, uint256 amount);
    event WithdrawableAdded(address indexed to, uint256 amount);
    event LoanCancelled();
    event Refund(address indexed investor, uint256 amount);

    modifier onlyBorrower() {
        require(msg.sender == borrower, "Only borrower");
        _;
    }

    modifier onlyWhen(LoanState s) {
        require(state == s, "Invalid state");
        _;
    }

    constructor(
        uint256 _loanId,
        address _borrower,
        address _stable,
        uint256 _principal,
        uint256 _minFund,
        uint256 _tenorDays,
        uint16 _profitShareBps,
        uint16 _markupBps,
        uint32 _sectorCode,
        uint8 _loanType,
        address _compliance
    ) {
        loanId = _loanId;
        borrower = _borrower;
        stable = IERC20(_stable);
        principal = _principal;
        minFund = _minFund;
        tenorDays = _tenorDays;
        profitShareBps = _profitShareBps;
        markupBps = _markupBps;
        sectorCode = _sectorCode;
        loanType = LoanType(_loanType);
        state = LoanState.FUNDRAISING;
        compliance = _compliance;
    }

    function fund(uint256 amount) external onlyWhen(LoanState.FUNDRAISING) {
        require(amount > 0, "Zero");
        uint256 beforeBal = stable.balanceOf(address(this));
        stable.transferFrom(msg.sender, address(this), amount);
        uint256 afterBal = stable.balanceOf(address(this));
        uint256 received = afterBal - beforeBal;
        if (contributions[msg.sender] == 0) investors.push(msg.sender);
        contributions[msg.sender] += received;
        funded += received;
        emit Funded(msg.sender, received, funded);
    }

    function activate() external onlyWhen(LoanState.FUNDRAISING) onlyBorrower {
        ComplianceRegistry reg = ComplianceRegistry(compliance);
        require(reg.loanApproved(loanId), "Not approved by scholar");
        require(funded >= minFund, "Not enough funds");
        state = LoanState.ACTIVE;
        activatedAt = block.timestamp;

        if (loanType == LoanType.MUDARABAH) {
            stable.transfer(borrower, funded);
        } else {
            stable.transfer(borrower, funded);
        }
        emit Activated(activatedAt);
    }

    function repay(uint256 amount) external {
        require(state == LoanState.ACTIVE, "Not active");
        require(amount > 0, "Zero");

        uint256 beforeBal = stable.balanceOf(address(this));
        stable.transferFrom(msg.sender, address(this), amount);
        uint256 afterBal = stable.balanceOf(address(this));
        uint256 received = afterBal - beforeBal;
        repaid += received;
        emit RepaymentMade(msg.sender, received);

        uint256 totalToDistribute = received;
        if (totalToDistribute > 0) {
            for (uint256 i = 0; i < investors.length; i++) {
                address inv = investors[i];
                uint256 share = (contributions[inv] * totalToDistribute) / funded;
                if (share > 0) {
                    _asyncTransfer(inv, share);
                    emit WithdrawableAdded(inv, share);
                }
            }
        }

        if (repaid >= funded + ((funded * markupBps) / 10000)) {
            state = LoanState.COMPLETED;
        }
    }

    function getInvestors() external view returns (address[] memory) {
        return investors;
    }

    function cancelAndRefund() external {
        require(state == LoanState.FUNDRAISING, "Not fundraising");
        require(msg.sender == borrower, "Only borrower");
        state = LoanState.CANCELLED;
        for (uint256 i = 0; i < investors.length; i++) {
            address inv = investors[i];
            uint256 amt = contributions[inv];
            if (amt > 0) {
                contributions[inv] = 0;
                stable.transfer(inv, amt);
                emit Refund(inv, amt);
            }
        }
        emit LoanCancelled();
    }

    function contributionOf(address who) external view returns (uint256) {
        return contributions[who];
    }
}

contracts/Loanfactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Loan.sol";
import "./ComplianceRegistry.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract LoanFactory is AccessControl {
    address[] public loans;
    ComplianceRegistry public compliance;

    event LoanCreated(address indexed loan, address indexed borrower, uint256 indexed loanId);

    uint256 public nextLoanId;

    constructor(address _compliance) {
        compliance = ComplianceRegistry(_compliance);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function createLoan(
        address stableAsset,
        uint256 principal,
        uint256 minFund,
        uint256 tenorDays,
        uint16 profitShareBps,
        uint16 markupBps,
        uint32 sectorCode,
        uint8 loanType // 0: MUDARABAH, 1: MURABAHA
    ) external returns (address) {
        require(compliance.sectorAllowed(sectorCode), "Sector not allowed");
        uint256 loanId = ++nextLoanId;
        Loan newLoan = new Loan(
            loanId,
            msg.sender,
            stableAsset,
            principal,
            minFund,
            tenorDays,
            profitShareBps,
            markupBps,
            sectorCode,
            loanType,
            address(compliance)
        );
        loans.push(address(newLoan));
        emit LoanCreated(address(newLoan), msg.sender, loanId);
        return address(newLoan);
    }

    function getLoans() external view returns (address[] memory) {
        return loans;
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

contracts/utils/PullPayment.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * Minimal PullPayment base contract.
 * Stores balances for payees and lets them withdraw later.
 */
abstract contract PullPayment {
    mapping(address => uint256) private _payments;

    event PaymentDeposited(address indexed payee, uint256 amount);
    event PaymentWithdrawn(address indexed payee, uint256 amount);

    function payments(address payee) public view returns (uint256) {
        return _payments[payee];
    }

    function _asyncTransfer(address payee, uint256 amount) internal virtual {
        require(payee != address(0), "Invalid payee");
        _payments[payee] += amount;
        emit PaymentDeposited(payee, amount);
    }

    function _withdrawPayment(address payable payee) internal virtual {
        uint256 payment = _payments[payee];
        require(payment > 0, "No payments due");

        _payments[payee] = 0;

        (bool success, ) = payee.call{value: payment}("");
        require(success, "Withdraw failed");

        emit PaymentWithdrawn(payee, payment);
    }
}

